IF stop = 1 THEN
  temp[0]:=0;
  temp[1]:=0;
  temp[2]:=0;
  temp[3]:=0;
  temp[4]:=0;
  temp[5]:=0;
  go_left:=0;
  go_right:=0;
END_IF;

IF entry_sensor = 1 THEN
  IF camera_blue=TRUE THEN
    temp[counterIn] := 1;
  ELSIF camera_green=TRUE THEN
    temp[counterIn] := 2;
  ELSIF camera_metal = TRUE THEN
    temp[counterIn] := 3;
  END_IF;
END_IF;

IF exit_sensor = 1 THEN
  IF temp[counterOut] = 1 THEN
    go_left := 1;
    go_right := 0;
  ELSIF temp[counterOut] = 2 THEN
    go_right := 1;
    go_left := 0;
  ELSIF temp[counterOut] = 3 THEN
    go_right := 0;
    go_left := 0;
  END_IF;
END_IF;
